--
-- 表的结构 `bookinfo`
--

DROP TABLE IF EXISTS `bookinfo`;
CREATE TABLE IF NOT EXISTS `bookinfo` (
  `id` int(4) NOT NULL,
  `book_name` varchar(30) COLLATE utf8_bin NOT NULL,
  `author` varchar(30) COLLATE utf8_bin NOT NULL,
  `book_press` varchar(30) COLLATE utf8_bin NOT NULL,
  `book_type` varchar(20) COLLATE utf8_bin NOT NULL,
  `add_date` date NOT NULL DEFAULT '2019-12-12' COMMENT '入库时间',
  `book_brief` text COLLATE utf8_bin,
  `book_photo` varchar(150) CHARACTER SET utf8 DEFAULT NULL COMMENT '图片路径',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=68 DEFAULT CHARSET=utf8 COLLATE=utf8_bin;

